  "expo": {
    "name": "OpenWhispr",
    "slug": "openwhispr-mobile",
    "version": "1.0.0",
    "orientation": "portrait",
    "icon": "./assets/images/icon.png",
    "scheme": "openwhispr",
    "userInterfaceStyle": "automatic",
    "newArchEnabled": true,
    "splash": {
      "image": "./assets/images/splash-icon.png",
      "resizeMode": "contain",
      "backgroundColor": "#ffffff"
    },
    "ios": {
      "supportsTablet": true,
      "bundleIdentifier": "com.openwhispr.mobile",
      "deploymentTarget": "17.0",
      "infoPlist": {
        "NSMicrophoneUsageDescription": "OpenWhispr needs microphone access to record your voice for speech-to-text transcription.",
        "UIBackgroundModes": [
          "audio"
        ]
      }
    },
    "plugins": [
      "expo-router",
      [
        "expo-av",
        {
          "microphonePermission": "OpenWhispr needs microphone access to record your voice for speech-to-text transcription."
        }
      ],
      "expo-secure-store",
      "expo-sqlite",
      "./plugins/withWhisperKit"
    ],
    "experiments": {
      "typedRoutes": true
    }
  }
}

