import { createMMKV, type MMKV } from "react-native-mmkv";
import { Platform } from "react-native";

const APP_GROUP_ID = "group.com.openwhispr.mobile";

/**
 * MMKV storage instance configured to use the App Group shared container.
 * This allows the keyboard extension to read settings written by the main app.
 *
 * On iOS, react-native-mmkv supports App Group paths via the `path` parameter.
 * The App Group container is at:
 *   ~/Library/Group Containers/group.com.openwhispr.mobile/
 */
